// ProductDetail.js
import React from 'react';

const ProductDetail = ({ product }) => {
    const isGlutenFree = product.labels_tags?.includes('en:gluten-free');
    const mayContainGluten = product.allergens_tags?.some(tag => ['en:wheat', 'en:barley', 'en:rye', 'en:oats'].includes(tag));

    return (
        <div>
            <h2>{product.product_name}</h2>
            <p>{isGlutenFree ? 'This product is labeled as gluten-free.' : 'This product is not specifically labeled as gluten-free.'}</p>
            {mayContainGluten && <p>Warning: May contain gluten-containing ingredients.</p>}
        </div>
    );
};

export default ProductDetail;
