// src/ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './App.css'; // Ensure this import path is correct

const ProductDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);
    const [alternatives, setAlternatives] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            setIsLoading(true);
            try {
                const response = await axios.get(`https://world.openfoodfacts.org/api/v0/product/${id}.json`);
                if (response.data && response.data.product) {
                    setProduct(response.data.product);
                    // Dynamically set the page background color based on gluten status
                    const backgroundColor = response.data.product.allergens_tags.includes('en:gluten') ? '#f8e0e0' :
                        (response.data.product.labels_tags?.includes('en:gluten-free') ? '#e0f8e0' : '#f8f0e0');
                    document.body.style.backgroundColor = backgroundColor;

                    if (response.data.product.allergens_tags.includes('en:gluten')) {
                        fetchGlutenFreeAlternatives(response.data.product.product_name);
                    }
                } else {
                    setError("Product not found");
                }
            } catch (error) {
                console.error("Error fetching product details:", error);
                setError("Error fetching product details");
            } finally {
                setIsLoading(false);
            }
        };

        fetchProduct();

        // Cleanup function to reset background color when component unmounts or before re-render
        return () => {
            document.body.style.backgroundColor = ''; // Reset to default background color
        };
    }, [id]);

    const fetchGlutenFreeAlternatives = async (productName) => {
        try {
            const query = encodeURIComponent(`${productName} gluten free`);
            const response = await axios.get(`https://world.openfoodfacts.org/cgi/search.pl?search_terms=${query}&action=process&json=1`);
            setAlternatives(response.data.products.slice(0, 5)); // Limiting to 5 alternatives for simplicity
        } catch (error) {
            console.error("Error fetching gluten-free alternatives:", error);
        }
    };

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="product-detail">
            <button onClick={() => navigate(-1)} style={{ marginBottom: '20px' }}>Back to Search</button>
            <h2>{product.product_name}</h2>
            {product.image_url && (
                <img src={product.image_url} alt={product.product_name} style={{ maxWidth: '200px', maxHeight: '200px' }} />
            )}
            <p>Brand: {product.brands}</p>
            {product.ingredients_text && (
                <>
                    <h3>Ingredients:</h3>
                    <p>{product.ingredients_text}</p>
                </>
            )}
            {product.allergens_tags.includes('en:gluten') ? (
                <>
                    <p>This product contains gluten.</p>
                    <h3>Gluten-Free Alternatives:</h3>
                    <ul>
                        {alternatives.length > 0 ? (
                            alternatives.map((alt) => (
                                <li key={alt.id}>
                                    {alt.image_url && (
                                        <img src={alt.image_url} alt={alt.product_name} style={{ maxWidth: '50px', maxHeight: '50px', marginRight: '10px' }} />
                                    )}
                                    {alt.product_name} - {alt.brands}
                                </li>
                            ))
                        ) : (
                            <p>No alternatives found.</p>
                        )}
                    </ul>
                </>
            ) : (
                <p>This product is gluten-free.</p>
            )}
        </div>
    );
};

export default ProductDetail;
