// src/ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './App.css'; // Ensure this import path is correct

const ProductDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);
    const [alternatives, setAlternatives] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            setIsLoading(true);
            try {
                const response = await axios.get(`https://world.openfoodfacts.org/api/v0/product/${id}.json`);
                if (response.data && response.data.product) {
                    setProduct(response.data.product);
                    // Determine background color and text based on gluten status
                    document.body.style.backgroundColor = getBackgroundColor(response.data.product);
                    if (response.data.product.allergens_tags?.includes('en:gluten')) {
                        fetchGlutenFreeAlternatives(response.data.product.product_name);
                    }
                } else {
                    setError("Product not found");
                }
            } catch (error) {
                console.error("Error fetching product details:", error);
                setError("Error fetching product details");
            } finally {
                setIsLoading(false);
            }
        };

        fetchProduct();

        // Cleanup: Reset background color when component unmounts
        return () => {
            document.body.style.backgroundColor = ''; // Reset to default
        };
    }, [id]);

    const fetchGlutenFreeAlternatives = async (productName) => {
        try {
            const query = encodeURIComponent(`${productName} gluten free`);
            const response = await axios.get(`https://world.openfoodfacts.org/cgi/search.pl?search_terms=${query}&action=process&json=1`);
            setAlternatives(response.data.products.slice(0, 5)); // Limit to 5 alternatives for simplicity
        } catch (error) {
            console.error("Error fetching gluten-free alternatives:", error);
        }
    };

    const getBackgroundColor = (product) => {
        if (product.labels_tags?.includes('en:gluten-free')) {
            return '#e0f8e0'; // Light green for gluten-free products
        } else if (product.allergens_tags?.includes('en:gluten')) {
            return '#f8e0e0'; // Light red for products containing gluten
        }
        return '#f8f0e0'; // Light orange for products not clearly labeled
    };

    const getGlutenStatusText = (product) => {
        if (product.labels_tags?.includes('en:gluten-free')) {
            return 'This product is clearly labeled as gluten-free.';
        } else if (product.allergens_tags?.includes('en:gluten')) {
            return 'This product contains gluten.';
        }
        return 'This product is not clearly labeled regarding its gluten content.';
    };

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="product-detail">
            <button onClick={() => navigate(-1)} style={{ marginBottom: '20px', cursor: 'pointer' }}>Back to Search</button>
            <h2>{product.product_name}</h2>
            {product.image_url && (
                <img src={product.image_url} alt={product.product_name} style={{ maxWidth: '200px', maxHeight: '200px', display: 'block', margin: 'auto' }} />
            )}
            <p>Brand: {product.brands}</p>
            {product.ingredients_text && (
                <>
                    <h3>Ingredients:</h3>
                    <p>{product.ingredients_text}</p>
                </>
            )}
            <p><strong>{getGlutenStatusText(product)}</strong></p>
            {product.allergens_tags?.includes('en:gluten') && (
                <>
                    <h3>Gluten-Free Alternatives:</h3>
                    <ul>
                        {alternatives.length > 0 ? (
                            alternatives.map((alt) => (
                                <li key={alt.id} style={{ textAlign: 'left' }}>
                                    {alt.image_url && (
                                        <img src={alt.image_url} alt={alt.product_name} style={{ maxWidth: '50px', maxHeight: '50px', marginRight: '10px', verticalAlign: 'middle' }} />
                                    )}
                                    {alt.product_name} - {alt.brands}
                                </li>
                            ))
                        ) : (
                            <p>No alternatives found.</p>
                        )}
                    </ul>
                </>
            )}
        </div>
    );
};

export default ProductDetail;
