// src/ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProductDetail = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [alternatives, setAlternatives] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            setIsLoading(true);
            try {
                const response = await axios.get(`https://world.openfoodfacts.org/api/v0/product/${id}.json`);
                if (response.data && response.data.product) {
                    setProduct(response.data.product);
                    // After setting the product, check if we need to fetch alternatives
                    if (response.data.product.allergens_tags.includes('en:gluten')) {
                        // Assuming using the product name to find alternatives for simplicity
                        fetchGlutenFreeAlternatives(response.data.product.product_name);
                    }
                } else {
                    setError("Product not found");
                }
            } catch (error) {
                console.error("Error fetching product details:", error);
                setError("Error fetching product details");
            } finally {
                setIsLoading(false);
            }
        };

        fetchProduct();
    }, [id]);

    const fetchGlutenFreeAlternatives = async (productName) => {
        try {
            const query = encodeURIComponent(`${productName} gluten free`);
            const response = await axios.get(`https://world.openfoodfacts.org/cgi/search.pl?search_terms=${query}&action=process&json=1`);
            setAlternatives(response.data.products.slice(0, 5)); // Limiting to 5 alternatives for simplicity
        } catch (error) {
            console.error("Error fetching gluten-free alternatives:", error);
            // Optionally handle this error state (e.g., show an error message)
        }
    };

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>{product.product_name}</h2>
            {/* Display product details */}
            {product.allergens_tags.includes('en:gluten') ? (
                <>
                    <p>This product contains gluten.</p>
                    <h3>Gluten-Free Alternatives:</h3>
                    <ul>
                        {alternatives.length > 0 ? (
                            alternatives.map((alt) => (
                                <li key={alt.id}>{alt.product_name}</li>
                            ))
                        ) : (
                            <p>No alternatives found.</p>
                        )}
                    </ul>
                </>
            ) : (
                <p>This product is gluten-free.</p>
            )}
        </div>
    );
};

export default ProductDetail;
