// src/ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProductDetail = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        setIsLoading(true);
        axios.get(`https://world.openfoodfacts.org/api/v0/product/${id}.json`)
            .then(response => {
                if (response.data && response.data.product) {
                    setProduct(response.data.product);
                } else {
                    setError("Product not found");
                }
                setIsLoading(false);
            })
            .catch(error => {
                console.error("Error fetching product details:", error);
                setError("Error fetching product details");
                setIsLoading(false);
            });
    }, [id]);

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    // Check for gluten-free label or allergens indicating gluten
    const isGlutenFree = product.labels_tags?.includes('en:gluten-free');
    const mayContainGluten = product.allergens_tags?.some(allergen => ['en:wheat', 'en:barley', 'en:rye', 'en:oats'].includes(allergen));

    return (
        <div>
            <h2>{product.product_name}</h2>
            <p>Brand: {product.brands}</p>
            {product.ingredients_text && (
                <div>
                    <h3>Ingredients:</h3>
                    <p>{product.ingredients_text}</p>
                </div>
            )}
            {isGlutenFree && <p>This product is labeled as gluten-free.</p>}
            {!isGlutenFree && mayContainGluten && <p>Warning: This product is not labeled gluten-free and may contain wheat, barley, rye, or oats.</p>}
            {!isGlutenFree && !mayContainGluten && <p>This product does not have gluten listed as an allergen.</p>}
            {/* Display more product details as needed */}
        </div>
    );
};

export default ProductDetail;
